import {status}from '../../models/status'
import axios from "axios";
export const addStatustoreducer=(status:status)=>{
return {
    type:"STATUSADD",
    payloud:status
}
}
export const initStatus=(status:status)=>{
    return {
        type:"STATUSINIT",
        payloud:status
    }
    }
export const deleteStatustoreducer=(status:status)=>{
    return {
        type:"STATUSDELETE",
        payloud:status
    }
     }
     export const fillStatustoreducer=(statuses:status[])=>{
        return {
            type:"STATUSFILL",
            payloud:statuses
        }
         }    
     export const addStatus = (status:status) => {
        return (dispatch: (arg0: any) => void) => {
        axios.post(`https://localhost:44330/api/status/add`,status).then((succ) => {
              console.log("s add",succ.data);
                dispatch(addStatus(status));
               
            }).catch((er) => {
              
                console.log(er.message)         
            })
        }
    }
    export const deleteStatus = (status:status) => {
        return (dispatch: (arg0: any) => void) => {
        axios.post(`https://localhost:44330/api/status/delete`,status).then((succ) => {
              console.log("s delete",succ.data);
                dispatch(deleteStatustoreducer(status));
               
            }).catch((er) => {
              
                console.log(er.message)         
            })
        }
    }
    export const  getStatuses = () => {
        return (dispatch: (arg0: { type: string; payloud: status[]; }) => void) => {
            axios.get(`https://localhost:44398/api/status/all`).then((succ) => {
                console.log([succ.data[0]]);
    
                dispatch(fillStatustoreducer([succ.data[0]]))
              
                // return succ.data.length;
            }).catch((er) => {
                console.log(er.message)
            })
        }
    }
