import { status } from "../../models/status";


const initilize={
    statuses:[new status("m"),new status("j")],
    init:null
}
export const statusReducer=(state=initilize,action: { type: any; payloud: status; })=>{
    switch(action.type){
        case "STATUSDELETE": let list:status[]= state.statuses.filter(s=>{return s.name!==action.payloud.name});
        let init = state.init && state.init !== action.payloud ? state.init : null;
        return{...state,statuses:list,init:init}
    case "STATUSINIT":
return{...state,select:action.payloud};
     case "STATUSADD":
    return{...state,songs:[...state.statuses,action.payloud]}
    
}
            
        
       
