const initilize={
    transitions:[]

}
export const transitionReducer=(state=initilize,action)=>{
    switch(action.type){
     case "TRANSITIONADD":
    return{...state,songs:[...state.transitions,action.payloud]}
    case "TRANSITIONDELETE":
    //    let s1= state.songs.filter(s=>{s===action.payloud})
    //    state.songs.map((s,i=>{s===s1?state.songs.splice(i,1):null}))
    let list= state.transitions.filter(t=>{t!==action.payloud})
        return{...state,transitions:list}
}
return state;
}
