import { status } from "../../models/status";


const initilize={
    statuses:[new status("m"),new status("j")],
    init:null
}
export const statusReducer=(state=initilize,action: { type: any; payloud: any; })=>{
    switch(action.type){
        case "STATUSDELETE": let  list:status[]= state.statuses.filter(s=>{s!==action.payloud});
        return{...state,statuses:list}
    case "STATUSINIT":
return{...state,select:action.payloud};
     case "STATUSADD":
    return{...state,songs:[...state.statuses,action.payloud]}
    
}
            
        
       
